INSERT INTO `apps` (`id`, `name`, `text`, `opisanie`, `img`, `css`, `link`) VALUES
(1, 'Николай Чудотворец', 'Приложение содержит акафист Николаю Чудотворцу, его житие и чудеса. А так же Вы можете узнать где покоятся мощи Святителя Николая и праздники Николая Чудотворца.\r\nБольше чудес Николая Чудотворца можно найти на сайте https://chudesanikolaya.ru', 'Приложение содержит акафист Николаю Чудотворцу, его житие и чудеса.', 'resourse\\assets\\img\\SN.jpg', 'SN', 'https://play.google.com/store/apps/details?id=alexbyvalaam.by.SNchudotvorec'),
(3, 'Сергий Радонежский', 'В приложении Вы найдёте акафист и житие преподобного Сергий Радонежского которого по праву считают всея Руси Чудотворцем. Он является основателем ряда православных монастырей, в том числе Троице-Сергиева лавры которая находится в Сергиевом-Посаде под Москвой. Считается покровителем учащихся, ему молятся чтобы сдать экзамены, поступить в учебные заведения. Но он помогает и в других ситуациях, главное молиться ему.', 'В приложении Вы найдёте акафист и житие преподобного Сергий Радонежского которого по праву считают всея Руси Чудотворцем. Он является основателем ряда православных монастырей, в том числе Троице-Сергиева лавры которая находится в Сергиевом-Посаде под Москвой. Считается покровителем учащихся, ему молятся чтобы сдать экзамены, поступить в учебные заведения.', 'resourse\\assets\\img\\SR.jpg', 'SR', 'https://play.google.com/store/apps/details?id=alexander.valaam.radonezh'),
(4, 'Матрона Московская', 'Матрона Московская – православная святая, имевшая от рождения дар чудотворения. Вся её жизнь стала примером великого духовного подвига любви, терпения, самоотречения и сострадания. Люди приезжали за помощью к матушке за десятки километров со своими болезнями, тревогами, скорбями. Поток паломников, чтобы приложиться к ее святым мощам, не иссякает и сегодня. Скачав приложение Вы можете почитать акафист блаженной Матроны Московской, ознакомится с её житием и чудесами которые произошли по молитвам Матроны Московской.', 'Матрона Московская – православная святая, имевшая от рождения дар чудотворения. Вся её жизнь стала примером великого духовного подвига любви, терпения, самоотречения и сострадания.', 'resourse\\assets\\img\\MM.jpg', 'MM', 'https://play.google.com/store/apps/details?id=alexander.valaam.matronamoskovskaya'),
(7, 'Ксения Петербургская', 'Приложение содержит акафист и житие с чудесами блаженной Ксении Петербургской. Ксения блаженная Петербургская — Ангел-хранитель Петербурга, любимая и широко почитаемая Святая, которая с 26-ти лет до 71 года без крова над головой бродила по городу, помогая людям, молясь за спасение души мужа, скончавшегося без покаяния из-за внезапности смерти. \r\nКсения Петербургская помогала людям в прошлом, помогает и в наше время. Об этом много свидетельств. Кто, что просит, то и получает: здоровье, решение с жильём, работой, мира в семье, девушки просят найти жениха и выйти замуж. Много у нас насущных трудностей. Помогает даже, если мы дома читаем Акафист.\r\nПримеры:\r\n* Молодой человек шёл на вокзал, чтобы поехать к тёще, попробовать помочь её горю: уволили (сократили) с работы. Внезапно, он сам не зная почему, пошёл к Храму и купил Акафист Ксении Петербургской. Пока ехал, прочитал несколько раз. Вопрос решился положительно. \r\n* Другой молодой человек, как поссорится со своей девушкой, едет к Часовне Ксении. Всегда приходит помощь, мир установлен. \r\n* В наше время одна семья, которая получила помощь, говорила, что ухватились за зелёную юбку Ксенюшки и всегда к ней с молитвой.', 'Приложение содержит акафист и житие с чудесами блаженной Ксении Петербургской.', 'resourse\\assets\\img\\KP.jpg', 'KP', 'https://play.google.com/store/apps/details?id=alexander.valaam.kseniyapeterbyrgskaya'),
(9, 'Кирилл и Мария Радонежские', 'В приложении Вы найдёте акафист и житие преподобных Кирилла и Марии Радонежских родителей преподобного Сергия Радонежского которого по праву считают всея Руси Чудотворцем. \r\nВ конце жизни Кирилл и Мария вместе приняли сначала иноческий постриг, а потом и схиму в Хотьковском Покровском монастыре в 3 км от Радонежа, в то время одновременно мужском и женском. Умерли в 1337 году (не позже начала 1340 годов) в старости, после болезней. \r\n«Прежде,  чем прийти ко мне, поклонитесь родителем моим», – еще при  жизни завещал преподобный Сергий Радонежский паломникам, желавшим получить от него утешение', 'В приложении Вы найдёте акафист и житие преподобных Кирилла и Марии Радонежских родителей преподобного Сергия Радонежского которого по праву считают всея Руси Чудотворцем.', 'resourse\\assets\\img\\KM.jpg', 'KMR', 'https://play.google.com/store/apps/details?id=alexander.valaam.kirillandmary');

